import React, { Component } from "react";
import AppInfo from "../../components/AppInfo/AppInfo";
import AppFilter from "../../components/AppFilter/AppFilter";
import SearchPanel from "../../components/SearchPanel/SearchPanel";
import EmployerList from "../../components/EmployersList/EmployerList";
import EmployersAddForm from "../../components/EmployersAddForm/EmployersAddForm";
import "./App.css";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [
                { name: "Иван Иванов", salary: "3231112", service: "Замена лампы", date: "2024-11-06" },
                { name: "Анна Смирнова", salary: "250000", service: "Ремонт генератора", date: "2024-11-07" },
            ], // Изначально есть некоторые данные
            term: "",
            filter: ""
        };
        this.maxId = 4; // Начальный id
    }

    // Функция для добавления нового элемента
    addItem = (name, salary, service, date) => {
        const newItem = {
            name,
            salary,
            service,
            date,
            id: this.maxId++,
        };

        this.setState(({ data }) => {
            return {
                data: [...data, newItem], // Добавление нового сотрудника в данные
            };
        });
    };

    deleteItem = (id) => {
        this.setState(({ data }) => {
            return {
                data: data.filter((item) => item.id !== id),
            };
        });
    };

    onToggleProp = (id, prop) => {
        this.setState(({ data }) => ({
            data: data.map((item) => {
                if (item.id === id) {
                    return { ...item, [prop]: !item[prop] };
                }
                return item;
            }),
        }));
    };

    searchEmp = (items, term) => {
        if (term.length === 0) {
            return items;
        }
        return items.filter((item) => {
            return item.name.indexOf(term) > -1;
        });
    };

    onUpdateSearch = (term) => {
        this.setState({ term });
    };

    filterPost = (items, filter) => {
        switch (filter) {
            case "rise":
                return items.filter((item) => item.rise);
            case "moreThan1000":
                return items.filter((item) => item.salary > 1000);
            default:
                return items;
        }
    };

    onFilterSelect = (filter) => {
        this.setState({ filter });
    };

    render() {
        const { data, term, filter } = this.state;
        const employees = this.state.data.length;
        const increased = this.state.data.filter((item) => item.increase).length;
        const visibleData = this.filterPost(this.searchEmp(data, term), filter);

        return (
            <div className="app">
                <AppInfo employees={employees} increased={increased} />
                <div className="searchPanel">
                    <SearchPanel onUpdateSearch={this.onUpdateSearch} />
                    <AppFilter filter={filter} onFilterSelect={this.onFilterSelect} />
                </div>
                <EmployerList
                    data={visibleData}
                    onDelete={this.deleteItem}
                    onToggleProp={this.onToggleProp}
                />
                <EmployersAddForm onAdd={this.addItem} />
            </div>
        );
    }
}

export default App;
