    const data =[
        {
            name: "Katy P.",
            salary: 1000,
            increase: false,
            id: 1
        },
        {
            name: "Alex W.",
            salary: 900,
            increase: true,
            id: 2
        },
        {
            name: "Sandra B.",
            salary: 2000,
            increase: false,
            id: 3
        },

]
    return (
        <div className="app">
            <AppInfo/>
            <div className="searchPanel">
            <SearchPanel/>
            <AppFilter/>
            </div>
            <EmployerList data={data}
            onDelete={id =>
            console.log(id)}
            />
            <EmployersAddForm/>
        </div>
    )
}

