import React, { useState, useEffect } from 'react';
import Select from 'react-select';
const SelectOptionsFromAPI = () => {
    const [options, setOptions] = useState([]);

    useEffect(() => {
      // Perform the API call and update the options state
      const fetchOptions = async () => {
        try {
          const response = await fetch('https://skyscanner44.p.rapidapi.com/autocomplete');
          const options = {
            method: 'GET',
            headers: {
                'X-RapidAPI-Key': '25f0d40abcmsh52a27d3eb75468cp1963e6jsnc0877b0ae6b6',
                'X-RapidAPI-Host': 'skyscanner44.p.rapidapi.com'
            }
        };
          const data = await response.json();
          setOptions(data);
        } catch (error) {
          console.error('Error fetching options:', error);
        }
      };

      fetchOptions();
    }, []);

    return (
      <Select options={options} />
    );
  };

  export default SelectOptionsFromAPI;
