import React,{Component} from "react";
// import "./EmployersListItems.css"
import "./EmoloyersListItem.css"
class EmployersListItem extends Component{
    // constructor(props){
    //     super(props);
    //     this.state = {
    //         increase: false
    //     }
    // }
    // onIncrease = () => {
    //     this.setState(({increase}) => ({increase: !increase}))
    // }
    render(){
        const {name, salary, onDelete, onToggleRise, onToggleIncrease} = this.props;

        const {increase} = this.state;
        let classNames="listGroupItem d-flex justifyContentBetween like";
    if(increase){
        classNames += ' increase';
    }
    return(
        <li className={classNames} >
            <span className="listGroupItemLabel" onClick={onToggleRise}>{name}</span>
            <input type="text" className="listGroupItemInput" defaultValue={salary + "$"} />
            <div className="dFlex justifyContentCenter alignItemCenter ">
                <button className="btnCookie btn-sm" type="button" onClick={onToggleIncrease}>
                <i className="fas fa-cookie"></i>
                </button>
                <button className="btnTrash btn-sm" type="button"
                onClick={onDelete}
                >
                <i className="fas fa-trash"></i>
                </button>
                <button className="btnStar btn-sm" type="button">
                <i className="fas fa-star"></i>
                </button>
            </div>
        </li>
    )
    }
}
export default EmployersListItem;
