import React, { Component } from "react";
import InputGroup from 'react-bootstrap/InputGroup';
import Button from 'react-bootstrap/Button';
import "./EmployersAddForm.css";

class EmployersAddForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            salary: "",
            service: "",
            selectedEmployee: "",
            employees: [
                { name: "Иван Иванов"},
                { name: "Анна Смирнова" },
                { name: "Петр Сидоров" }
            ]
        };
    }

    onValueChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }

    onEmployeeSelect = (e) => {
        const selectedEmployee = this.state.employees.find(emp => emp.name === e.target.value);
        if (selectedEmployee) {
            this.setState({
                name: selectedEmployee.name,
                salary: selectedEmployee.salary,
                service: selectedEmployee.service
            });
        }
    }

    onSubmit = (e) => {
        e.preventDefault();
        const { name, salary, service } = this.state;
        if (name.length < 3 || !salary) return;

        this.props.onAdd(name, salary, service);
        this.setState({
            name: '',
            salary: '',
            service: ''
        });
    }

    render() {
        const { name, salary, service, employees } = this.state;
        return (
            <div className="appAddForm">
                <h3>Добавьте нового сотрудника</h3>
                <form className="addForm dFlex" onSubmit={this.onSubmit}>
                    <InputGroup className="mb-3">
                        <select onChange={this.onEmployeeSelect} className="formControl newPostLabel">
                            <option value="">Выберите сотрудника</option>
                            {employees.map((emp, index) => (
                                <option key={index} value={emp.name}>{emp.name}</option>
                            ))}
                        </select>

                        <input type="text" className="formControl newPostLabel" placeholder="Имя сотрудника"
                            name="name" value={name} onChange={this.onValueChange} />

                        <input type="text" className="formControl newPostLabel" placeholder="Название услуги"
                            name="service" value={service} onChange={this.onValueChange} />

                        <input type="number" className="formControl newPostLabel" placeholder="Зарплата в тенге"
                            name="salary" value={salary} onChange={this.onValueChange} />

                        <Button type="submit" className="btn btnOutsideLight" variant="warning">Добавить</Button>
                    </InputGroup>
                </form>
            </div>
        );
    }
}

export default EmployersAddForm;
