import React, { useState, useEffect } from 'react';
import Select from 'react-select';
const SelectOptionsFromAPI = () => {
    const [response, setResponse] = useState();
    const [options, setOptions] = useState([]);
    const [searchValue, setSearchValue] = useState('');

    useEffect(() => {
      const fetchOptions = async () => {

            try {
              const response = await fetch(
                `https://skyscanner44.p.rapidapi.com/autocomplete?query=${searchValue}`,
                {
                  method: "GET",
                  headers: {
                    'X-RapidAPI-Key': 'a6046e630emsh8d5e8a83e7d6144p1375b0jsna9b7da7739c5',
	            	'X-RapidAPI-Host': 'skyscanner44.p.rapidapi.com',
                    "Content-Type": "application/json",
                  },
                }
              );
              const data = await response.json();
              setOptions(data);
              console.log(data[0]);
            } catch (error) {
              return error;
              console.error('Error fetching options:', error);
            }



      };

      fetchOptions();
    }, [searchValue]);
    const handleInputChange = (e) => {
        setSearchValue(e.target.value);
      };
    return (

         <div>
      <input type="text" value={searchValue} onChange={handleInputChange} placeholder="Search by name" />
      {/* <Select options={options} /> */}
    </div>


    );
  };

  export default SelectOptionsFromAPI;

