import React,{useState, useEffect} from "react";
// import SelectOptionsFromAPI from './SelectOptionsFromAPI';
// import BurgerMenu from "./BurgerMenu";
function App() {
  const [data, setData] = useState([]); // State to store the fetched data
  const [searchValue, setSearchValue] = useState(''); // State to store the search input value
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          `https://skyscanner44.p.rapidapi.com/autocomplete?query=${searchValue}`,
          {
            method: 'GET',
            headers: {
              'X-RapidAPI-Key': '6400de408cmshdf91a09a2c0357ep17b5a0jsn22f5ceda2a6b',
              'X-RapidAPI-Host': 'skyscanner44.p.rapidapi.com',
              'Content-Type': 'application/json',
            },
          }
        );
        const jsonData = await response.json();
        setData(jsonData); // Update the state with the fetched data
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData(); // Fetch data when the component mounts or when the search value changes
  }, [searchValue]);
  return (
    <div>
      <input
        type="text"
        value={searchValue}
        onChange={(e) => setSearchValue(e.target.value)}
        placeholder="Search..."
      />

      <ul>
        {data.map((item) => (
          <li key={item.id} id={item.id}>{item.iata_code}{item.name}</li>
        ))}
      </ul>
    </div>
  );


}

export default App;
