import React,{useState, useEffect} from "react";
import SelectOptionsFromAPI from './SelectOptionsFromAPI';
import BurgerMenu from "./BurgerMenu";
function App() {
  const [inputValue, setInputValue] = useState('');
  const [filteredCounties, setFilteredCounties] = useState([]);
  useEffect(() => {
    if (!Array.isArray(filteredCounties)) {
      // Handle the case when filteredCounties is not an array
      return null; // or display an error message, render a loading state, etc.
    }
    const fetchCounties = async () => {
      try {
        const response = await fetch(
          `https://skyscanner44.p.rapidapi.com/autocomplete?query=${inputValue}`,
          {
            method: 'GET',
            headers: {
              'X-RapidAPI-Key': 'a6046e630emsh8d5e8a83e7d6144p1375b0jsna9b7da7739c5',
              'X-RapidAPI-Host': 'skyscanner44.p.rapidapi.com',
              'Content-Type': 'application/json',
            },
          }
        );
        const data = await response.json();
        // setFilteredCounties(data);
        if (Array.isArray(data)) {
          setFilteredCounties(data);
        } else {
          console.error('Invalid response format. Expected an array.');
        }
      } catch (error) {
        console.error('Error fetching counties:', error);
      }
    };
    fetchCounties();
  }, [inputValue]);

  const handleFilter = () => {
    // Filter counties based on user input
    const filtered = filteredCounties.filter((county) =>
      county.name.toLowerCase().includes(inputValue.toLowerCase())
    );
    setFilteredCounties(filtered);
  };

  return (
    <div>
    <input
      type="text"
      value={inputValue}
      onChange={(e) => setInputValue(e.target.value)}
    />
    <button onClick={handleFilter}>Filter</button>
    <ul>
      {filteredCounties.map((county) => (
        <li key={county.id}>{county.name}</li>
      ))}
    </ul>
  </div>


  );
}

export default App;

