import React,{Component} from "react";
import InputGroup from 'react-bootstrap/InputGroup';
import Button from 'react-bootstrap/Button';
import "./EmployersAddForm.css"
// function EmployersAddForm (){
//     return(
//         <div className="appAddForm">
//             <h3>Добавьте нового сотрудника</h3>
//             <form className="addForm dFlex">
//                 <input type="text" className="formControl newPostLabel" placeholder="Как его зовут?" />
//                 <input type="number" className="formControl newPostLabel" placeholder="З/П в $?" />
//                 <button type="submit" className="btn btnOutsideLight">Добавить</button>
//             </form>
//         </div>
//     )
// }
class EmployersAddForm extends Component{
    constructor (props) {
        super(props);
        this.state ={
            name: "",
            salary: ""
        }
    }
    onValueChange = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        })
    }
    onSubmit = (e) => {
        e.preventDefault();
        // Можно еще и сообщения добавлять, подсветку, атрибуты minlength и тд.
        if (this.state.name.length < 3 || !this.state.salary) return;
        this.props.onAdd(this.state.name, this.state.salary);
        this.setState({
            name: '',
            salary: ''
        })
    }
    // static onLog = () => {
    //     console.log("Hi");
    // }
    // static logged = "on";
  render () {
    const {name, salary} = this.state
    return(
        <div className="appAddForm">
            <h3>Добавьте нового сотрудника</h3>

            <form className="addForm dFlex"
             onSubmit = {this.onSubmit}>

                  <InputGroup className="mb-3">
                <input type="text" className="formControl newPostLabel" placeholder="Как его зовут?"
                name="name"
                value={name}
                onChange={this.onValueChange} />
                <input type="number" className="formControl newPostLabel" placeholder="З/П в $?"
                name="salary"
                value={salary}
                onChange={this.onValueChange}
                />
                <Button type="submit" className="btn btnOutsideLight" variant="secondary" size="lg" active>Добавить</Button>

                </InputGroup>


            </form>
        </div>
    )
  }
}
// EmployersAddForm.onLog();
// console.log(EmployersAddForm.logged);
export default EmployersAddForm;
