class EmployersAddForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            salary: "",
            service: "",
            date: "",
            selectedEmployee: "",
            employees: [
                { name: "Иван Иванов", salary: "", service: "", date: "" },
                { name: "Анна Смирнова", salary: "", service: "", date: "" },
                { name: "Петр Сидоров", salary: "", service: "", date: "" }
            ],
            services: ["Программист", "Дизайнер", "Менеджер", "Аналитик"]
        };
    }

    onValueChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value || ""  // Убедитесь, что пустое значение становится ""
        });
    }

    onEmployeeSelect = (e) => {
        const selectedEmployee = this.state.employees.find(emp => emp.name === e.target.value);
        this.setState({
            name: selectedEmployee?.name || "",
            salary: selectedEmployee?.salary || "",
            service: selectedEmployee?.service || "",
            date: selectedEmployee?.date || ""
        });
    }

    onSubmit = (e) => {
        e.preventDefault();
        const { name, salary, service, date } = this.state;
        if (name.length < 3 || !salary || !date) return;

        const newEmployee = { name, salary, service, date };

        const updatedEmployees = [...this.state.employees, newEmployee];
        const jsonData = JSON.stringify(updatedEmployees);
        console.log("Сохраненные данные:", jsonData);

        this.setState({
            employees: updatedEmployees,
            name: '',
            salary: '',
            service: '',
            date: ''
        });
    }

    render() {
        const { name, salary, service, date, employees, services } = this.state;
        return (
            <div className="appAddForm">
                <h3>Добавьте нового сотрудника</h3>
                <form className="addForm dFlex" onSubmit={this.onSubmit}>
                    <InputGroup className="mb-3">
                        <select onChange={this.onEmployeeSelect} className="formControl newPostLabel">
                            <option value="">Выберите сотрудника</option>
                            {employees.map((emp, index) => (
                                <option key={index} value={emp.name}>{emp.name}</option>
                            ))}
                        </select>

                        <input type="text" className="formControl newPostLabel" placeholder="Имя сотрудника"
                            name="name" value={name || ""} onChange={this.onValueChange} />

                        <select onChange={this.onServiceSelect} className="formControl newPostLabel">
                            <option value="">Выберите услугу</option>
                            {services.map((srv, index) => (
                                <option key={index} value={srv}>{srv}</option>
                            ))}
                        </select>

                        <input type="text" className="formControl newPostLabel" placeholder="Название услуги"
                            name="service" value={service || ""} onChange={this.onValueChange} />

                        <input type="number" className="formControl newPostLabel" placeholder="Зарплата в тенге"
                            name="salary" value={salary || ""} onChange={this.onValueChange} />

                        <input type="date" className="formControl newPostLabel"
                            name="date" value={date || ""} onChange={this.onValueChange} />

                        <Button type="submit" className="btn btnOutsideLight" variant="warning">Добавить</Button>
                    </InputGroup>
                </form>
            </div>
        );
    }
}

export default EmployersAddForm;
