          <div className="controls">
            <button onClick={this.plusNumber}>INC</button>
            <button onClick={this.minusNumber}>DEC</button>
            <button onClick={this.randomNumber}>RND</button>
            <button onClick={this.resetNumber}>RESET</button>
          </div>
        </div>
      )
    }
  }



