// export default EmployersAddForm;
class EmployersAddForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            salary: "",
            service: "",
            date: ""
        };
    }

    onValueChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };

    onSubmit = (e) => {
        e.preventDefault();
        const { name, salary, service, date } = this.state;

        // Проверка, что все поля заполнены
        if (name.length < 3 || !salary || !service || !date) {
            alert("Пожалуйста, заполните все поля!");
            return;
        }

        // Отправляем данные в App
        this.props.onAdd(name, salary, service, date);

        // Очищаем форму после добавления
        this.setState({
            name: "",
            salary: "",
            service: "",
            date: ""
        });
    };

    render() {
        const { employees, services } = this.props;
        const { name, salary, service, date } = this.state;

        return (
            <div className="appAddForm">
                <h3>Добавьте нового сотрудника</h3>
                <form className="addForm dFlex" onSubmit={this.onSubmit}>
                    <InputGroup className="mb-3">
                        {/* Список сотрудников */}
                        <select name="name" value={name} onChange={this.onValueChange}>
                            <option value="">Выберите сотрудника</option>
                            {employees.map((employee, index) => (
                                <option key={index} value={employee.name}>
                                    {employee.name}
                                </option>
                            ))}
                        </select>

                        {/* Поле для услуги */}
                        <select 
                            name="service" 
                            value={service} 
                            onChange={this.onValueChange}>
                            <option value="">Выберите услугу</option>
                            {services.map((service, index) => (
                                <option key={index} value={service}>
                                    {service}
                                </option>
                            ))}
                        </select>

                        {/* Поле для зарплаты */}
                        <input
                            type="number"
                            className="formControl newPostLabel"
                            placeholder="Стоимость услуги в тенге?"
                            name="salary"
                            value={salary}
                            onChange={this.onValueChange}
                        />

                        {/* Поле для даты */}
                        <input
                            type="date"
                            className="formControl newPostLabel"
                            name="date"
                            value={date}
                            onChange={this.onValueChange}
                        />

                        {/* Кнопка добавления */}
                        <Button type="submit" className="btn btnOutsideLight" variant="warning">
                            Добавить
                        </Button>
                    </InputGroup>
                </form>
            </div>
        );
    }
}

export default EmployersAddForm;
