import React,{useState, useEffect} from "react";
// import SelectOptionsFromAPI from './SelectOptionsFromAPI';
// import BurgerMenu from "./BurgerMenu";
function App() {
  const [data, setData] = useState([]); 
  const [searchValue, setSearchValue] = useState(''); 
  const [selectedResult, setSelectedResult] = useState("");
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          `https://skyscanner44.p.rapidapi.com/autocomplete?query=${searchValue}`,
          {
            method: 'GET',
            headers: {
              'X-RapidAPI-Key': '5f1769bb70msh0d6a819ccca8070p19def0jsn30c6ab7daf7c',
              'X-RapidAPI-Host': 'skyscanner44.p.rapidapi.com',
              'Content-Type': 'application/json',
            },
          }
        );
        const jsonData = await response.json();
        console.log(jsonData);
        setData(jsonData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData(); 
  }, [searchValue]);
  const handleSearchChange = (e) => {
    setSearchValue(e.target.value);
  };
  const handleResultClick = (result) => {
    setSelectedResult(result);
    setSearchValue(result.name);
  };
  return (
    <div>
      <input
        type="text"
        // key={searchValue}
        value={searchValue}
        // onChange={(e) => setSearchValue(e.target.value)}
        onChange={handleSearchChange}
        placeholder="Search..."
      />

      {/* <ul>
        {data.map((item) => (
          <li key={item.id} id={item.id}>

            {item.iata_code} 

            </li>
        ))}
      </ul> */}
        <ul>
        {data.map((item) => (
          <li
            key={item.id}
            id={item.id}
            onClick={() => handleResultClick(item)}
          >
            {item.iata_code}
          </li>
        ))}
      </ul>

      <div>Selected Result: {selectedResult.name}</div>
    </div>
  );
}

export default App;
