                    rise: true,
                    id: 2
                },
                {
                    name: "Sandra B.",
                    salary: 2000,
                    increase: false,
                    rise: true,
                    id: 3
                },


        ],
        term: "",
        filter: "rise"
        }
        this.maxId=4;
    }
    addItem = (name,salary) => {
        const newItem = {
            name, 
            salary, 
            increase: false, 
            rise: false,
            id: this.maxId++
        }
        this.setState(({data}) => {
            const newArr = [...data, newItem];
            return{
                data: newArr
            }
        })
    }
    deleteItem =(id) => {
        this.setState(({data}) => {
            // const index = data.findIndex(elem => elem.id === id);
